SELECT SUM("On_Time_On_Time_Performance_2016_1"."ArrDel15") as agg_0, AVG("On_Time_On_Time_Performance_2016_1"."Div1TotalGTime" + "On_Time_On_Time_Performance_2016_1"."CRSArrTime") as agg_1, MAX("On_Time_On_Time_Performance_2016_1"."ArrDelayMinutes") as agg_2 FROM "L_AIRLINE_ID" LEFT OUTER JOIN "On_Time_On_Time_Performance_2016_1" ON "L_AIRLINE_ID"."Code" = "On_Time_On_Time_Performance_2016_1"."AirlineID" LEFT OUTER JOIN "L_AIRPORT_ID" ON "On_Time_On_Time_Performance_2016_1"."Div1AirportID" = "L_AIRPORT_ID"."Code" LEFT OUTER JOIN "L_STATE_FIPS" ON "On_Time_On_Time_Performance_2016_1"."DestStateFips" = "L_STATE_FIPS"."Code"  WHERE "On_Time_On_Time_Performance_2016_1"."DepTimeBlk" IN ('1100-1159', '0800-0859', '1000-1059', '1700-1759', '1200-1259', '0900-0959', '1500-1559', '0600-0659', '1300-1359') AND "On_Time_On_Time_Performance_2016_1"."UniqueCarrier" IN ('UA', 'EV', 'B6', 'DL') AND "L_AIRLINE_ID"."Description" LIKE '%Air%'