SELECT AVG("On_Time_On_Time_Performance_2016_1"."DivArrDelay") as agg_0, SUM("On_Time_On_Time_Performance_2016_1"."FirstDepTime") as agg_1, AVG("On_Time_On_Time_Performance_2016_1"."TotalAddGTime") as agg_2 FROM "L_AIRPORT_SEQ_ID" LEFT OUTER JOIN "On_Time_On_Time_Performance_2016_1" ON "L_AIRPORT_SEQ_ID"."Code" = "On_Time_On_Time_Performance_2016_1"."Div2AirportSeqID" LEFT OUTER JOIN "L_CITY_MARKET_ID" ON "On_Time_On_Time_Performance_2016_1"."OriginCityMarketID" = "L_CITY_MARKET_ID"."Code"  WHERE "L_AIRPORT_SEQ_ID"."Description" IN ('Canon City, CO: Fremont County', 'Mulhouse, France: EuroAirport', 'Chakhcharan, Afghanistan: Chakhcharan Airport', 'Hudiksvall, Sweden: Hudiksvall Airport', 'Akiachak, AK: Akiachak Airport', 'Chiayi, Taiwan: Chiayi Airport', 'Cotulla, TX: Cotulla-La Salle County', 'North Adams, MA: Harriman-and-West') AND "L_AIRPORT_SEQ_ID"."Code" >= 1343307