SELECT MAX("L_CITY_MARKET_ID"."Code") as agg_0, MAX("L_CITY_MARKET_ID"."Code") as agg_1 FROM "L_CITY_MARKET_ID"  WHERE "L_CITY_MARKET_ID"."Description" = 'Benbecula, United Kingdom' AND "L_CITY_MARKET_ID"."Code" BETWEEN 31980 AND 356695 AND ("L_CITY_MARKET_ID"."Description" = 'Great Falls, MT' OR "L_CITY_MARKET_ID"."Description" NOT LIKE '%Island,%' OR "L_CITY_MARKET_ID"."Description" NOT LIKE '%Papua%')