SELECT MAX("On_Time_On_Time_Performance_2016_1"."Div2AirportID" + "On_Time_On_Time_Performance_2016_1"."AirlineID") as agg_0, MIN("On_Time_On_Time_Performance_2016_1"."Year" + "On_Time_On_Time_Performance_2016_1"."TaxiOut") as agg_1 FROM "L_AIRLINE_ID" LEFT OUTER JOIN "On_Time_On_Time_Performance_2016_1" ON "L_AIRLINE_ID"."Code" = "On_Time_On_Time_Performance_2016_1"."AirlineID" LEFT OUTER JOIN "L_AIRPORT_SEQ_ID" ON "On_Time_On_Time_Performance_2016_1"."Div2AirportSeqID" = "L_AIRPORT_SEQ_ID"."Code" LEFT OUTER JOIN "L_AIRPORT_ID" ON "On_Time_On_Time_Performance_2016_1"."Div2AirportID" = "L_AIRPORT_ID"."Code" LEFT OUTER JOIN "L_CANCELLATION" ON "On_Time_On_Time_Performance_2016_1"."CancellationCode" = "L_CANCELLATION"."Code"  WHERE "L_CANCELLATION"."Code" <= 2 AND "L_AIRPORT_ID"."Description" NOT LIKE '%I%nternational%' AND "L_AIRLINE_ID"."Description" IN ('Sunworld International Airlines: SM', 'DHL Aero Expresso: DHQ')