SELECT AVG("On_Time_On_Time_Performance_2016_1"."DivAirportLandings" + "On_Time_On_Time_Performance_2016_1"."DepTime") as agg_0 FROM "L_CITY_MARKET_ID" LEFT OUTER JOIN "On_Time_On_Time_Performance_2016_1" ON "L_CITY_MARKET_ID"."Code" = "On_Time_On_Time_Performance_2016_1"."DestCityMarketID" LEFT OUTER JOIN "L_AIRLINE_ID" ON "On_Time_On_Time_Performance_2016_1"."AirlineID" = "L_AIRLINE_ID"."Code" LEFT OUTER JOIN "L_AIRPORT_SEQ_ID" ON "On_Time_On_Time_Performance_2016_1"."DestAirportSeqID" = "L_AIRPORT_SEQ_ID"."Code" LEFT OUTER JOIN "L_DISTANCE_GROUP_250" ON "On_Time_On_Time_Performance_2016_1"."DistanceGroup" = "L_DISTANCE_GROUP_250"."Code"  WHERE "L_CITY_MARKET_ID"."Code" <= 199688 AND "L_AIRLINE_ID"."Description" != 'Star Aviation Corporation: STR' AND "On_Time_On_Time_Performance_2016_1"."Origin" LIKE '%LAX%' AND "L_DISTANCE_GROUP_250"."Code" != 15 AND "L_AIRPORT_SEQ_ID"."Description" NOT LIKE '%International%' AND "On_Time_On_Time_Performance_2016_1"."Dest" IN ('PHX', 'ATL', 'LAX')