SELECT MAX("On_Time_On_Time_Performance_2016_1"."DivArrDelay") as agg_0, SUM("On_Time_On_Time_Performance_2016_1"."Year") as agg_1 FROM "L_DISTANCE_GROUP_250" LEFT OUTER JOIN "On_Time_On_Time_Performance_2016_1" ON "L_DISTANCE_GROUP_250"."Code" = "On_Time_On_Time_Performance_2016_1"."DistanceGroup"  WHERE "L_DISTANCE_GROUP_250"."Description" NOT LIKE '%Gr%eater%' AND "On_Time_On_Time_Performance_2016_1"."DestStateName" NOT LIKE '%Color%ado%' AND ("On_Time_On_Time_Performance_2016_1"."DepTimeBlk" LIKE '%2000%-2059%' OR ("On_Time_On_Time_Performance_2016_1"."Div2Airport" = 'DTW' AND "On_Time_On_Time_Performance_2016_1"."DepTimeBlk" != '2100-2159')) AND "L_DISTANCE_GROUP_250"."Code" BETWEEN 38 AND 40