SELECT MIN("On_Time_On_Time_Performance_2016_1"."CarrierDelay") as agg_0, SUM("On_Time_On_Time_Performance_2016_1"."AirlineID") as agg_1 FROM "L_AIRPORT_SEQ_ID" LEFT OUTER JOIN "On_Time_On_Time_Performance_2016_1" ON "L_AIRPORT_SEQ_ID"."Code" = "On_Time_On_Time_Performance_2016_1"."OriginAirportSeqID" LEFT OUTER JOIN "L_CANCELLATION" ON "On_Time_On_Time_Performance_2016_1"."CancellationCode" = "L_CANCELLATION"."Code" LEFT OUTER JOIN "L_CITY_MARKET_ID" ON "On_Time_On_Time_Performance_2016_1"."OriginCityMarketID" = "L_CITY_MARKET_ID"."Code"  WHERE "L_CANCELLATION"."Description" NOT LIKE '%Secur%ity%' AND ("On_Time_On_Time_Performance_2016_1"."Origin" IN ('ORD', 'LAS', 'IAH') OR "On_Time_On_Time_Performance_2016_1"."Diverted" BETWEEN 0 AND 1) AND "L_AIRPORT_SEQ_ID"."Description" NOT LIKE '%Cit%y%' AND ("L_CITY_MARKET_ID"."Description" LIKE '%Jap%an%' OR "L_CITY_MARKET_ID"."Description" = 'Lake Havasu City, AZ' OR "L_CITY_MARKET_ID"."Description" != 'Manicore, Brazil')