SELECT MIN("On_Time_On_Time_Performance_2016_1"."DayOfWeek") as agg_0 FROM "L_ONTIME_DELAY_GROUPS" LEFT OUTER JOIN "On_Time_On_Time_Performance_2016_1" ON "L_ONTIME_DELAY_GROUPS"."Code" = "On_Time_On_Time_Performance_2016_1"."DepartureDelayGroups" LEFT OUTER JOIN "L_STATE_FIPS" ON "On_Time_On_Time_Performance_2016_1"."OriginStateFips" = "L_STATE_FIPS"."Code"  WHERE "On_Time_On_Time_Performance_2016_1"."OriginCityName" IN ('Denver, CO', 'San Francisco, CA', 'Atlanta, GA', 'Houston, TX', 'Las Vegas, NV') AND "L_ONTIME_DELAY_GROUPS"."Description" LIKE '%4%4%' AND "L_ONTIME_DELAY_GROUPS"."Description" != 'Delay between 90 to 104 minutes' AND ("On_Time_On_Time_Performance_2016_1"."Carrier" NOT LIKE '%AA%' OR "On_Time_On_Time_Performance_2016_1"."Carrier" LIKE '%AA%')