SELECT SUM("On_Time_On_Time_Performance_2016_1"."Quarter") as agg_0, COUNT(*) as agg_1, AVG("On_Time_On_Time_Performance_2016_1"."CRSElapsedTime" + "On_Time_On_Time_Performance_2016_1"."DestWac") as agg_2 FROM "L_AIRLINE_ID" LEFT OUTER JOIN "On_Time_On_Time_Performance_2016_1" ON "L_AIRLINE_ID"."Code" = "On_Time_On_Time_Performance_2016_1"."AirlineID"  WHERE ("L_AIRLINE_ID"."Description" NOT LIKE '%Trans%' OR ("L_AIRLINE_ID"."Description" IN ('Federal Express Corporation: FX') AND "L_AIRLINE_ID"."Description" IN ('Antonov Company: ADB', 'Federal Express Corporation: FX', 'Mackey International Inc.: MAC', 'Ecuatoriana De Aviacion: EU', 'Blue Scandinavia Ab: TRQ', 'Northern Air Car Inc.: NC'))) AND "On_Time_On_Time_Performance_2016_1"."Flights" BETWEEN 1 AND 2 AND "On_Time_On_Time_Performance_2016_1"."OriginWac" BETWEEN 67 AND 81 AND "L_AIRLINE_ID"."Description" IN ('Southern Frontier Air Trans: SFQ (1)', 'Northern Air Car Inc.: NC', 'Antonov Company: ADB', 'Federal Express Corporation: FX', 'Mackey International Inc.: MAC') AND ("On_Time_On_Time_Performance_2016_1"."Origin" LIKE '%DFW%' OR "On_Time_On_Time_Performance_2016_1"."Origin" NOT LIKE '%PD%X%' OR "On_Time_On_Time_Performance_2016_1"."Origin" NOT LIKE '%LAX%') AND "On_Time_On_Time_Performance_2016_1"."Dest" LIKE '%TPA%'