SELECT COUNT(*) as agg_0, COUNT(*) as agg_1, AVG("On_Time_On_Time_Performance_2016_1"."DepDelay") as agg_2 FROM "L_AIRPORT_ID" LEFT OUTER JOIN "On_Time_On_Time_Performance_2016_1" ON "L_AIRPORT_ID"."Code" = "On_Time_On_Time_Performance_2016_1"."Div1AirportID" LEFT OUTER JOIN "L_AIRPORT_SEQ_ID" ON "On_Time_On_Time_Performance_2016_1"."Div1AirportSeqID" = "L_AIRPORT_SEQ_ID"."Code" LEFT OUTER JOIN "L_AIRLINE_ID" ON "On_Time_On_Time_Performance_2016_1"."AirlineID" = "L_AIRLINE_ID"."Code"  WHERE "L_AIRLINE_ID"."Code" >= 23136 AND ("L_AIRPORT_ID"."Description" != 'Gizan, Saudi Arabia: Gizan Saudia Arabia' OR ("L_AIRPORT_ID"."Description" IN ('Mulhouse, France: EuroAirport', 'Port Frederick, AK: Port Frederick Airport', 'Barrow, AK: Ice Strip') AND "L_AIRPORT_ID"."Description" IN ('Three Forks, MT: Three Forks Airport', 'Port Frederick, AK: Port Frederick Airport', 'Biloela, Australia: Biloela (Thanol)', 'Tarbes/Lourdes/Pyrenees, France: Tarbes-Lourdes-Pyrenees')))