SELECT AVG("On_Time_On_Time_Performance_2016_1"."CRSElapsedTime" + "On_Time_On_Time_Performance_2016_1"."ActualElapsedTime") as agg_0 FROM "L_AIRLINE_ID" LEFT OUTER JOIN "On_Time_On_Time_Performance_2016_1" ON "L_AIRLINE_ID"."Code" = "On_Time_On_Time_Performance_2016_1"."AirlineID" LEFT OUTER JOIN "L_ONTIME_DELAY_GROUPS" ON "On_Time_On_Time_Performance_2016_1"."DepartureDelayGroups" = "L_ONTIME_DELAY_GROUPS"."Code"  WHERE "L_ONTIME_DELAY_GROUPS"."Description" = 'Delay between 165 to 179 minutes' OR "L_ONTIME_DELAY_GROUPS"."Description" != 'Delay < -15 minutes'