SELECT COUNT(*) as agg_0, COUNT(*) as agg_1 FROM "L_AIRPORT_SEQ_ID" LEFT OUTER JOIN "On_Time_On_Time_Performance_2016_1" ON "L_AIRPORT_SEQ_ID"."Code" = "On_Time_On_Time_Performance_2016_1"."DestAirportSeqID" LEFT OUTER JOIN "L_AIRPORT_ID" ON "On_Time_On_Time_Performance_2016_1"."DestAirportID" = "L_AIRPORT_ID"."Code" LEFT OUTER JOIN "L_CITY_MARKET_ID" ON "On_Time_On_Time_Performance_2016_1"."DestCityMarketID" = "L_CITY_MARKET_ID"."Code" LEFT OUTER JOIN "L_ONTIME_DELAY_GROUPS" ON "On_Time_On_Time_Performance_2016_1"."DepartureDelayGroups" = "L_ONTIME_DELAY_GROUPS"."Code"  WHERE "L_AIRPORT_ID"."Description" = 'Naya, Japan: Naya Airport' AND "L_AIRPORT_SEQ_ID"."Description" LIKE '%Ne%w%' AND "L_AIRPORT_SEQ_ID"."Description" NOT LIKE '%P%ort%' AND "L_ONTIME_DELAY_GROUPS"."Description" = 'Delay between 150 to 164 minutes' AND "L_CITY_MARKET_ID"."Description" != 'Comanche, TX'