SELECT AVG("On_Time_On_Time_Performance_2016_1"."OriginCityMarketID") as agg_0, MAX("On_Time_On_Time_Performance_2016_1"."Quarter") as agg_1, SUM("On_Time_On_Time_Performance_2016_1"."CarrierDelay" + "On_Time_On_Time_Performance_2016_1"."ArrDelayMinutes") as agg_2 FROM "L_CANCELLATION" LEFT OUTER JOIN "On_Time_On_Time_Performance_2016_1" ON "L_CANCELLATION"."Code" = "On_Time_On_Time_Performance_2016_1"."CancellationCode" LEFT OUTER JOIN "L_AIRPORT_ID" ON "On_Time_On_Time_Performance_2016_1"."DestAirportID" = "L_AIRPORT_ID"."Code"  WHERE "L_CANCELLATION"."Description" IN ('Weather', 'National Air System') AND "L_CANCELLATION"."Code" != 11 AND ("On_Time_On_Time_Performance_2016_1"."DepTimeBlk" IN ('0900-0959', '1200-1259', '0700-0759', '0800-0859', '1000-1059') OR ("On_Time_On_Time_Performance_2016_1"."DestCityMarketID" >= 295161 AND "On_Time_On_Time_Performance_2016_1"."DepTimeBlk" NOT LIKE '%2300%-2359%')) AND ("L_AIRPORT_ID"."Description" LIKE '%County%' OR "L_AIRPORT_ID"."Code" >= 405937)