SELECT AVG("On_Time_On_Time_Performance_2016_1"."DayofMonth" + "On_Time_On_Time_Performance_2016_1"."Div2LongestGTime") as agg_0, MAX("On_Time_On_Time_Performance_2016_1"."DivArrDelay") as agg_1, COUNT(*) as agg_2 FROM "L_STATE_FIPS" LEFT OUTER JOIN "On_Time_On_Time_Performance_2016_1" ON "L_STATE_FIPS"."Code" = "On_Time_On_Time_Performance_2016_1"."OriginStateFips" LEFT OUTER JOIN "L_AIRPORT_ID" ON "On_Time_On_Time_Performance_2016_1"."OriginAirportID" = "L_AIRPORT_ID"."Code" LEFT OUTER JOIN "L_CITY_MARKET_ID" ON "On_Time_On_Time_Performance_2016_1"."OriginCityMarketID" = "L_CITY_MARKET_ID"."Code" LEFT OUTER JOIN "L_CANCELLATION" ON "On_Time_On_Time_Performance_2016_1"."CancellationCode" = "L_CANCELLATION"."Code"  WHERE "L_STATE_FIPS"."Description" LIKE '%Atoll%' AND "L_CANCELLATION"."Code" BETWEEN 1 AND 6