SELECT MAX("On_Time_On_Time_Performance_2016_1"."DepartureDelayGroups" + "On_Time_On_Time_Performance_2016_1"."CRSElapsedTime") as agg_0 FROM "L_AIRPORT_ID" LEFT OUTER JOIN "On_Time_On_Time_Performance_2016_1" ON "L_AIRPORT_ID"."Code" = "On_Time_On_Time_Performance_2016_1"."Div2AirportID" LEFT OUTER JOIN "L_AIRPORT_SEQ_ID" ON "On_Time_On_Time_Performance_2016_1"."Div2AirportSeqID" = "L_AIRPORT_SEQ_ID"."Code"  WHERE "On_Time_On_Time_Performance_2016_1"."Month" BETWEEN 1 AND 2 AND "On_Time_On_Time_Performance_2016_1"."CancellationCode" <= 11.779348477776008