SELECT SUM("On_Time_On_Time_Performance_2016_1"."FlightNum" + "On_Time_On_Time_Performance_2016_1"."CRSDepTime") as agg_0, COUNT(*) as agg_1 FROM "On_Time_On_Time_Performance_2016_1" LEFT OUTER JOIN "L_AIRPORT_SEQ_ID" ON "On_Time_On_Time_Performance_2016_1"."Div2AirportSeqID" = "L_AIRPORT_SEQ_ID"."Code" LEFT OUTER JOIN "L_AIRPORT_ID" ON "On_Time_On_Time_Performance_2016_1"."DestAirportID" = "L_AIRPORT_ID"."Code"  WHERE "L_AIRPORT_ID"."Description" = 'Manihi, French Polynesia: Manihi Airport'