SELECT MIN("On_Time_On_Time_Performance_2016_1"."Distance") as agg_0, COUNT(*) as agg_1 FROM "L_DISTANCE_GROUP_250" LEFT OUTER JOIN "On_Time_On_Time_Performance_2016_1" ON "L_DISTANCE_GROUP_250"."Code" = "On_Time_On_Time_Performance_2016_1"."DistanceGroup" LEFT OUTER JOIN "L_STATE_FIPS" ON "On_Time_On_Time_Performance_2016_1"."DestStateFips" = "L_STATE_FIPS"."Code" LEFT OUTER JOIN "L_AIRLINE_ID" ON "On_Time_On_Time_Performance_2016_1"."AirlineID" = "L_AIRLINE_ID"."Code" LEFT OUTER JOIN "L_AIRPORT_ID" ON "On_Time_On_Time_Performance_2016_1"."OriginAirportID" = "L_AIRPORT_ID"."Code"  WHERE "L_DISTANCE_GROUP_250"."Description" != '2500 Miles and Greater' AND ("L_AIRPORT_ID"."Description" = 'Joplin, MO: Joplin Regional' OR "L_AIRPORT_ID"."Description" != 'San Die, CA: San Die International') AND ("On_Time_On_Time_Performance_2016_1"."Origin" NOT LIKE '%JFK%' OR "On_Time_On_Time_Performance_2016_1"."Origin" IN ('SFO', 'LAS')) AND "L_DISTANCE_GROUP_250"."Code" != 47 AND "L_AIRLINE_ID"."Description" NOT LIKE '%(%1):%' AND "On_Time_On_Time_Performance_2016_1"."FlightDate" NOT LIKE '%2016-01-25%'