SELECT MIN("L_AIRLINE_ID"."Code") as agg_0, MAX("L_AIRLINE_ID"."Code") as agg_1, SUM("L_AIRLINE_ID"."Code") as agg_2 FROM "L_AIRLINE_ID"  WHERE ("L_AIRLINE_ID"."Description" IN ('Blue Scandinavia Ab: TRQ', 'DHL Aero Expresso: DHQ', 'Antonov Company: ADB', 'Mackey International Inc.: MAC', 'Northern Air Car Inc.: NC') OR "L_AIRLINE_ID"."Description" NOT LIKE '%(1):%') AND "L_AIRLINE_ID"."Code" <= 24629 AND "L_AIRLINE_ID"."Description" = 'Air Tungaru Corporation: VK'