SELECT COUNT(*) as agg_0, AVG("On_Time_On_Time_Performance_2016_1"."Div1WheelsOn") as agg_1 FROM "L_AIRPORT_ID" LEFT OUTER JOIN "On_Time_On_Time_Performance_2016_1" ON "L_AIRPORT_ID"."Code" = "On_Time_On_Time_Performance_2016_1"."DestAirportID" LEFT OUTER JOIN "L_CITY_MARKET_ID" ON "On_Time_On_Time_Performance_2016_1"."OriginCityMarketID" = "L_CITY_MARKET_ID"."Code" LEFT OUTER JOIN "L_AIRPORT_SEQ_ID" ON "On_Time_On_Time_Performance_2016_1"."DestAirportSeqID" = "L_AIRPORT_SEQ_ID"."Code"  WHERE "L_AIRPORT_ID"."Description" LIKE '%CA:%' AND "L_CITY_MARKET_ID"."Description" LIKE '%India%' AND "L_CITY_MARKET_ID"."Code" BETWEEN 30513 AND 153613 AND "L_AIRPORT_SEQ_ID"."Description" IN ('Seletar, Singapore: Seletar Airport')