SELECT MIN("On_Time_On_Time_Performance_2016_1"."DepartureDelayGroups") as agg_0 FROM "L_AIRLINE_ID" LEFT OUTER JOIN "On_Time_On_Time_Performance_2016_1" ON "L_AIRLINE_ID"."Code" = "On_Time_On_Time_Performance_2016_1"."AirlineID" LEFT OUTER JOIN "L_AIRPORT_ID" ON "On_Time_On_Time_Performance_2016_1"."Div2AirportID" = "L_AIRPORT_ID"."Code" LEFT OUTER JOIN "L_AIRPORT_SEQ_ID" ON "On_Time_On_Time_Performance_2016_1"."DestAirportSeqID" = "L_AIRPORT_SEQ_ID"."Code"  WHERE "On_Time_On_Time_Performance_2016_1"."CRSDepTime" BETWEEN 780 AND 2201 AND "L_AIRPORT_ID"."Description" LIKE '%TX%:%'