SELECT MIN("On_Time_On_Time_Performance_2016_1"."WeatherDelay" + "On_Time_On_Time_Performance_2016_1"."CRSElapsedTime") as agg_0, MAX("On_Time_On_Time_Performance_2016_1"."ArrDelayMinutes") as agg_1 FROM "L_AIRLINE_ID" LEFT OUTER JOIN "On_Time_On_Time_Performance_2016_1" ON "L_AIRLINE_ID"."Code" = "On_Time_On_Time_Performance_2016_1"."AirlineID" LEFT OUTER JOIN "L_AIRPORT_ID" ON "On_Time_On_Time_Performance_2016_1"."Div2AirportID" = "L_AIRPORT_ID"."Code" LEFT OUTER JOIN "L_CANCELLATION" ON "On_Time_On_Time_Performance_2016_1"."CancellationCode" = "L_CANCELLATION"."Code" LEFT OUTER JOIN "L_CITY_MARKET_ID" ON "On_Time_On_Time_Performance_2016_1"."OriginCityMarketID" = "L_CITY_MARKET_ID"."Code"  WHERE "On_Time_On_Time_Performance_2016_1"."OriginCityName" LIKE '%OR%' AND "L_AIRPORT_ID"."Description" IN ('Barrow, AK: Ice Strip', 'Mulhouse, France: EuroAirport', 'Biloela, Australia: Biloela (Thanol)', 'New Bight, The Bahamas: New Bight Airport', 'Alpine, AK: Alpine Airstrip', 'Ugashik, AK: Ugashik Airport', 'Three Forks, MT: Three Forks Airport', 'Yuma, AZ: Yuma MCAS/Yuma International', 'Tarbes/Lourdes/Pyrenees, France: Tarbes-Lourdes-Pyrenees') AND "L_CANCELLATION"."Description" IN ('National Air System', 'Carrier') AND ("L_AIRPORT_ID"."Description" IN ('Alpine, AK: Alpine Airstrip', 'Ugashik, AK: Ugashik Airport', 'Mulhouse, France: EuroAirport', 'Tarbes/Lourdes/Pyrenees, France: Tarbes-Lourdes-Pyrenees') OR "L_AIRPORT_ID"."Description" NOT LIKE '%Australia:%') AND "L_CITY_MARKET_ID"."Code" BETWEEN 172101 AND 313065