SELECT SUM("On_Time_On_Time_Performance_2016_1"."DepDelayMinutes" + "On_Time_On_Time_Performance_2016_1"."Year") as agg_0, AVG("On_Time_On_Time_Performance_2016_1"."DestStateFips") as agg_1 FROM "L_ONTIME_DELAY_GROUPS" LEFT OUTER JOIN "On_Time_On_Time_Performance_2016_1" ON "L_ONTIME_DELAY_GROUPS"."Code" = "On_Time_On_Time_Performance_2016_1"."DepartureDelayGroups" LEFT OUTER JOIN "L_AIRPORT_SEQ_ID" ON "On_Time_On_Time_Performance_2016_1"."Div1AirportSeqID" = "L_AIRPORT_SEQ_ID"."Code"  WHERE "L_ONTIME_DELAY_GROUPS"."Description" != 'Delay between -15 and -1 minutes'