SELECT MIN("L_DISTANCE_GROUP_250"."Code") as agg_0 FROM "L_DISTANCE_GROUP_250"  WHERE ("L_DISTANCE_GROUP_250"."Description" NOT LIKE '%1500-1749%' OR "L_DISTANCE_GROUP_250"."Description" IN ('500-749 Miles', '1750-1999 Miles', '2000-2249 Miles')) AND "L_DISTANCE_GROUP_250"."Description" LIKE '%750-99%9%' AND "L_DISTANCE_GROUP_250"."Code" >= 49