SELECT MIN("On_Time_On_Time_Performance_2016_1"."DayOfWeek") as agg_0, SUM("On_Time_On_Time_Performance_2016_1"."TaxiIn") as agg_1 FROM "L_AIRPORT_ID" LEFT OUTER JOIN "On_Time_On_Time_Performance_2016_1" ON "L_AIRPORT_ID"."Code" = "On_Time_On_Time_Performance_2016_1"."OriginAirportID"  WHERE "L_AIRPORT_ID"."Description" = 'Chica, IL: Meigs Field' AND "L_AIRPORT_ID"."Description" = 'Fairmont, MN: Fairmont Municipal' AND "L_AIRPORT_ID"."Code" >= 168609 AND ("On_Time_On_Time_Performance_2016_1"."DestStateName" LIKE '%Col%orado%' OR "On_Time_On_Time_Performance_2016_1"."DestStateName" NOT LIKE '%Virgini%a%' OR "On_Time_On_Time_Performance_2016_1"."DestStateName" IN ('Nevada', 'Georgia', 'New York', 'Arizona', 'Colorado', 'Florida', 'North Carolina', 'Illinois')) AND "On_Time_On_Time_Performance_2016_1"."DestAirportID" <= 14177 AND "On_Time_On_Time_Performance_2016_1"."Div2AirportSeqID" BETWEEN 1154926.6083814786 AND 1288903.0