SELECT MIN("L_AIRPORT_ID"."Code" + "On_Time_On_Time_Performance_2016_1"."TaxiIn") as agg_0, COUNT(*) as agg_1 FROM "L_AIRLINE_ID" LEFT OUTER JOIN "On_Time_On_Time_Performance_2016_1" ON "L_AIRLINE_ID"."Code" = "On_Time_On_Time_Performance_2016_1"."AirlineID" LEFT OUTER JOIN "L_AIRPORT_SEQ_ID" ON "On_Time_On_Time_Performance_2016_1"."DestAirportSeqID" = "L_AIRPORT_SEQ_ID"."Code" LEFT OUTER JOIN "L_STATE_FIPS" ON "On_Time_On_Time_Performance_2016_1"."OriginStateFips" = "L_STATE_FIPS"."Code" LEFT OUTER JOIN "L_AIRPORT_ID" ON "On_Time_On_Time_Performance_2016_1"."Div2AirportID" = "L_AIRPORT_ID"."Code"  WHERE "L_STATE_FIPS"."Description" NOT LIKE '%Alaska%' AND ("L_AIRPORT_SEQ_ID"."Description" = 'Peoria, IL: Greater Peoria' OR "L_AIRPORT_SEQ_ID"."Description" LIKE '%Fort%' OR "L_AIRPORT_SEQ_ID"."Description" LIKE '%N%ew%')