SELECT AVG("On_Time_On_Time_Performance_2016_1"."DepartureDelayGroups" + "On_Time_On_Time_Performance_2016_1"."Quarter") as agg_0, MIN("On_Time_On_Time_Performance_2016_1"."DepTime" + "On_Time_On_Time_Performance_2016_1"."FirstDepTime") as agg_1 FROM "L_CANCELLATION" LEFT OUTER JOIN "On_Time_On_Time_Performance_2016_1" ON "L_CANCELLATION"."Code" = "On_Time_On_Time_Performance_2016_1"."CancellationCode"  WHERE "On_Time_On_Time_Performance_2016_1"."DestAirportSeqID" >= 1596513 AND "L_CANCELLATION"."Description" IN ('Weather', 'National Air System', 'Security') AND "L_CANCELLATION"."Code" = 15