SELECT AVG("L_ONTIME_DELAY_GROUPS"."Code") as agg_0, MAX("L_ONTIME_DELAY_GROUPS"."Code") as agg_1, MIN("L_ONTIME_DELAY_GROUPS"."Code") as agg_2 FROM "L_ONTIME_DELAY_GROUPS"  WHERE "L_ONTIME_DELAY_GROUPS"."Description" != 'Delay between -15 and -1 minutes' AND ("L_ONTIME_DELAY_GROUPS"."Description" != 'Delay >= 180 minutes' OR ("L_ONTIME_DELAY_GROUPS"."Code" = 35 AND "L_ONTIME_DELAY_GROUPS"."Description" IN ('Delay < -15 minutes', 'Delay between 15 to 29 minutes', 'Delay between 60 to 74 minutes', 'Delay between 105 to 119 minutes', 'Delay between 0 and 14 minutes', 'Delay between 45 to 59 minutes', 'Delay between 75 to 89 minutes')))