SELECT MIN("On_Time_On_Time_Performance_2016_1"."NASDelay" + "On_Time_On_Time_Performance_2016_1"."ArrivalDelayGroups") as agg_0, AVG("On_Time_On_Time_Performance_2016_1"."DivAirportLandings") as agg_1 FROM "L_AIRPORT_ID" LEFT OUTER JOIN "On_Time_On_Time_Performance_2016_1" ON "L_AIRPORT_ID"."Code" = "On_Time_On_Time_Performance_2016_1"."Div2AirportID" LEFT OUTER JOIN "L_CITY_MARKET_ID" ON "On_Time_On_Time_Performance_2016_1"."DestCityMarketID" = "L_CITY_MARKET_ID"."Code"  WHERE "On_Time_On_Time_Performance_2016_1"."Div1AirportID" <= 14376.95615609211 AND "L_CITY_MARKET_ID"."Description" IN ('Keyes Point, AK', 'Sealing Point, AK', 'Grass Valley, CA', 'Pittsfield, ME', 'Westminster, MD', 'Shingletown, CA', 'Afognak Lake, AK', 'Nimiuk Point, AK') AND "L_AIRPORT_ID"."Description" = 'New Orleans, LA: New Orleans NAS JRB' AND "On_Time_On_Time_Performance_2016_1"."DestStateName" LIKE '%Pue%rto%' AND "On_Time_On_Time_Performance_2016_1"."Diverted" != 0 AND "L_CITY_MARKET_ID"."Description" NOT LIKE '%T%X%'