
SELECT MAX("fieldingpost"."CS") as agg_0 FROM "fieldingpost" LEFT OUTER JOIN "players" ON "fieldingpost"."playerID" = "players"."playerID" LEFT OUTER JOIN "schoolsplayers" ON "players"."playerID" = "schoolsplayers"."playerID" LEFT OUTER JOIN "fielding" ON "players"."playerID" = "fielding"."playerID"  WHERE "players"."nameFirst" NOT LIKE '%Ha%rry%' AND "players"."birthCountry" LIKE '%D.R.%' AND "fieldingpost"."lgID" IN ('AL') AND "fielding"."lgID" = '%AL%' AND ("players"."deathCity" NOT LIKE '%Ci%ty%' OR "players"."deathCity" != '%Stuart%' OR "players"."deathCity" NOT LIKE '%Sa%n%') AND "players"."lahmanID" BETWEEN 2871 AND 7838 AND "schoolsplayers"."yearMin" BETWEEN 1078 AND 1930 AND "schoolsplayers"."schoolID" LIKE '%t%exas%';