
SELECT COUNT(*) as agg_0 FROM "halloffame" LEFT OUTER JOIN "players" ON "halloffame"."hofID" = "players"."hofID" LEFT OUTER JOIN "fielding" ON "players"."playerID" = "fielding"."playerID" LEFT OUTER JOIN "batting" ON "players"."playerID" = "batting"."playerID" LEFT OUTER JOIN "appearances" ON "players"."playerID" = "appearances"."playerID" LEFT OUTER JOIN "fieldingpost" ON "players"."playerID" = "fieldingpost"."playerID"  WHERE "fieldingpost"."teamID" LIKE '%A%TL%' AND "fielding"."WP" IS NOT NULL AND "halloffame"."needed" >= 172.5647880018823 AND "batting"."teamID" IN ('PHI', 'CHA', 'CLE', 'NYA', 'PIT', 'SLN', 'BOS') AND ("fielding"."POS" IN ('RF', 'C', '2B', 'SS', '1B', 'LF') OR "fielding"."POS" NOT LIKE '%DH%') AND "appearances"."playerID" IS NOT NULL;