
SELECT MIN("teams"."attendance") as agg_0 FROM "batting" LEFT OUTER JOIN "teams" ON "batting"."teamID" = "teams"."teamID" LEFT OUTER JOIN "managers" ON "teams"."teamID" = "managers"."teamID" LEFT OUTER JOIN "seriespost" ON "teams"."teamID" = "seriespost"."teamIDwinner" LEFT OUTER JOIN "awardssharemanagers" ON "managers"."managerID" = "awardssharemanagers"."managerID"  WHERE ("managers"."yearID" >= 2011 OR "managers"."yearID" BETWEEN 1905 AND 1985) AND "seriespost"."lgIDwinner" NOT LIKE '%A%L%' AND "seriespost"."teamIDwinner" LIKE '%FL%O%' AND "awardssharemanagers"."lgID" NOT LIKE '%A%L%' AND ("awardssharemanagers"."managerID" IN ('johnsda02m', 'larusto01m') OR ("awardssharemanagers"."awardID" IN ('Mgr of the year') AND "awardssharemanagers"."managerID" NOT LIKE '%showabu99m%')) AND "teams"."teamID" = '%MID%';