
SELECT AVG("salaries"."yearID") as agg_0 FROM "teams" LEFT OUTER JOIN "seriespost" ON "teams"."teamID" = "seriespost"."teamIDwinner" LEFT OUTER JOIN "salaries" ON "teams"."teamID" = "salaries"."teamID" LEFT OUTER JOIN "managers" ON "teams"."teamID" = "managers"."teamID" LEFT OUTER JOIN "managershalf" ON "managers"."managerID" = "managershalf"."managerID" LEFT OUTER JOIN "teamshalf" ON "teams"."teamID" = "teamshalf"."teamID" LEFT OUTER JOIN "allstarfull" ON "teams"."teamID" = "allstarfull"."teamID" LEFT OUTER JOIN "awardsmanagers" ON "managers"."managerID" = "awardsmanagers"."managerID" LEFT OUTER JOIN "batting" ON "teams"."teamID" = "batting"."teamID"  WHERE ("seriespost"."yearID" >= 1997 OR ("seriespost"."yearID" <= 2009 AND "seriespost"."yearID" BETWEEN 1942 AND 1966)) AND "teamshalf"."divID" = '%E%' AND "seriespost"."teamIDwinner" NOT LIKE '%SEA%' AND "batting"."lgID" IN ('UA', 'FL', 'AL', 'AA') AND "managershalf"."managerID" IS NOT NULL AND "allstarfull"."teamID" IS NOT NULL AND "awardsmanagers"."managerID" IS NOT NULL;