
SELECT MIN("teams"."G") as agg_0 FROM "teamsfranchises" LEFT OUTER JOIN "els_teamnames" ON "teamsfranchises"."franchID" = "els_teamnames"."franchid" LEFT OUTER JOIN "teams" ON "els_teamnames"."teamid" = "teams"."teamID" LEFT OUTER JOIN "teamshalf" ON "teams"."teamID" = "teamshalf"."teamID" LEFT OUTER JOIN "managers" ON "teams"."teamID" = "managers"."teamID"  WHERE "managers"."lgID" NOT LIKE '%A%L%' AND "teams"."teamIDBR" LIKE '%TOR%' AND "managers"."managerID" IN ('larusto01m', 'harribu01m', 'mauchge01m', 'mckecbi01m') AND "teams"."park" NOT LIKE '%Polo%' AND "teamshalf"."divID" = '%W%' AND "els_teamnames"."park" IN ('County Stadium', 'South End Grounds III', 'Ebbets Field', ''Sportsman's Park I'', 'Oriole Park', 'Jefferson Street Grounds', 'Recreation Park', 'Washington Park I', 'League Park I') AND "teamshalf"."DivWin" = '%N%';