
SELECT SUM("teams"."3B") as agg_0 FROM "els_teamnames" LEFT OUTER JOIN "teamsfranchises" ON "els_teamnames"."franchid" = "teamsfranchises"."franchID" LEFT OUTER JOIN "teams" ON "els_teamnames"."teamid" = "teams"."teamID" LEFT OUTER JOIN "seriespost" ON "teams"."teamID" = "seriespost"."teamIDwinner" LEFT OUTER JOIN "salaries" ON "teams"."teamID" = "salaries"."teamID" LEFT OUTER JOIN "allstarfull" ON "teams"."teamID" = "allstarfull"."teamID"  WHERE "teamsfranchises"."franchName" = '%Indianapolis Hoosiers%' AND "salaries"."lgID" NOT LIKE '%A%L%' AND "els_teamnames"."franchid" NOT LIKE '%C%HC%' AND ("teams"."lgID" LIKE '%N%L%' OR "teams"."lgID" LIKE '%AA%' OR "teams"."lgID" IN ('PL')) AND "els_teamnames"."id" <= 100 AND "seriespost"."teamIDwinner" IS NOT NULL AND "allstarfull"."teamID" IS NOT NULL;