
SELECT MIN("fieldingpost"."yearID") as agg_0 FROM "batting" LEFT OUTER JOIN "players" ON "batting"."playerID" = "players"."playerID" LEFT OUTER JOIN "fieldingpost" ON "players"."playerID" = "fieldingpost"."playerID" LEFT OUTER JOIN "awardsplayers" ON "players"."playerID" = "awardsplayers"."playerID" LEFT OUTER JOIN "schoolsplayers" ON "players"."playerID" = "schoolsplayers"."playerID" LEFT OUTER JOIN "teams" ON "batting"."teamID" = "teams"."teamID" LEFT OUTER JOIN "pitching" ON "players"."playerID" = "pitching"."playerID" LEFT OUTER JOIN "seriespost" ON "teams"."teamID" = "seriespost"."teamIDwinner" LEFT OUTER JOIN "pitchingpost" ON "players"."playerID" = "pitchingpost"."playerID" LEFT OUTER JOIN "salaries" ON "teams"."teamID" = "salaries"."teamID"  WHERE ("players"."deathYear" <= 2003.5732708606058 OR ("players"."deathYear" >= 1927.7223832124819 AND "players"."deathYear" >= 1948.0421642139536)) AND "pitching"."lgID" LIKE '%A%L%' AND "fieldingpost"."TP" = 0 AND ("batting"."teamID" IN ('PHI', 'SLN', 'CIN', 'BOS', 'CHA', 'NYA', 'DET', 'PIT') OR "batting"."teamID" LIKE '%ML4%' OR "batting"."teamID" != '%MIL%') AND "salaries"."teamID" != '%ML4%' AND "batting"."RBI" BETWEEN 0.0 AND 37.0301227531141 AND "batting"."stint" BETWEEN 1 AND 3 AND "awardsplayers"."playerID" IS NOT NULL AND "schoolsplayers"."playerID" IS NOT NULL AND "seriespost"."teamIDwinner" IS NOT NULL AND "pitchingpost"."playerID" IS NOT NULL;