
SELECT SUM("battingpost"."3B") as agg_0 FROM "battingpost" LEFT OUTER JOIN "players" ON "battingpost"."playerID" = "players"."playerID" LEFT OUTER JOIN "fieldingof" ON "players"."playerID" = "fieldingof"."playerID" LEFT OUTER JOIN "fieldingpost" ON "players"."playerID" = "fieldingpost"."playerID" LEFT OUTER JOIN "batting" ON "players"."playerID" = "batting"."playerID" LEFT OUTER JOIN "teams" ON "batting"."teamID" = "teams"."teamID" LEFT OUTER JOIN "fielding" ON "players"."playerID" = "fielding"."playerID" LEFT OUTER JOIN "appearances" ON "players"."playerID" = "appearances"."playerID" LEFT OUTER JOIN "schoolsplayers" ON "players"."playerID" = "schoolsplayers"."playerID" LEFT OUTER JOIN "teamshalf" ON "teams"."teamID" = "teamshalf"."teamID"  WHERE "teamshalf"."divID" = '%W%' AND "schoolsplayers"."schoolID" NOT LIKE '%u%cla%' AND "fieldingpost"."POS" != '%OF%' AND "fieldingpost"."yearID" BETWEEN 1972 AND 1997 AND "batting"."lgID" IN ('PL', 'UA', 'NL', 'FL') AND "battingpost"."round" IN ('WS', 'NLDS1', 'NLCS', 'ALDS1', 'ALCS', 'NWDIV', 'AEDIV') AND "fieldingpost"."lgID" IN ('AL') AND ("batting"."AB" >= 200.11736790331943 OR "batting"."AB" >= 2.4010396792425173) AND "fieldingof"."playerID" IS NOT NULL AND "fielding"."playerID" IS NOT NULL AND "appearances"."playerID" IS NOT NULL;