
SELECT SUM("appearances"."G_dh") as agg_0 FROM "schoolsplayers" LEFT OUTER JOIN "schools" ON "schoolsplayers"."schoolID" = "schools"."schoolID" LEFT OUTER JOIN "players" ON "schoolsplayers"."playerID" = "players"."playerID" LEFT OUTER JOIN "awardsshareplayers" ON "players"."playerID" = "awardsshareplayers"."playerID" LEFT OUTER JOIN "awardsplayers" ON "players"."playerID" = "awardsplayers"."playerID" LEFT OUTER JOIN "appearances" ON "players"."playerID" = "appearances"."playerID"  WHERE "awardsplayers"."lgID" IN ('AA', 'NL', 'ML') AND "appearances"."lgID" IN ('FL', 'AA', 'PL') AND "awardsplayers"."yearID" >= 2002 AND "schools"."schoolID" IS NOT NULL AND "awardsshareplayers"."playerID" IS NOT NULL;