
SELECT COUNT(*) as agg_0 FROM "players" LEFT OUTER JOIN "appearances" ON "players"."playerID" = "appearances"."playerID" LEFT OUTER JOIN "pitchingpost" ON "players"."playerID" = "pitchingpost"."playerID" LEFT OUTER JOIN "battingpost" ON "players"."playerID" = "battingpost"."playerID" LEFT OUTER JOIN "fieldingof" ON "players"."playerID" = "fieldingof"."playerID" LEFT OUTER JOIN "batting" ON "players"."playerID" = "batting"."playerID"  WHERE "battingpost"."teamID" IN ('OAK', 'LAN', 'PHI', 'CHN', 'CIN', 'BOS', 'NYA', 'SLN', 'ATL') AND "appearances"."teamID" LIKE '%HOU%' AND "batting"."lgID" NOT LIKE '%AA%' AND "pitchingpost"."teamID" IN ('CIN', 'BOS', 'SFN', 'NYA') AND "appearances"."lgID" LIKE '%N%L%' AND "players"."deathCity" LIKE '%Chicago%' AND "fieldingof"."playerID" IS NOT NULL;