
SELECT MAX("players"."birthDay") as agg_0 FROM "managers" LEFT OUTER JOIN "managershalf" ON "managers"."managerID" = "managershalf"."managerID" LEFT OUTER JOIN "awardsmanagers" ON "managers"."managerID" = "awardsmanagers"."managerID" LEFT OUTER JOIN "awardssharemanagers" ON "managers"."managerID" = "awardssharemanagers"."managerID" LEFT OUTER JOIN "teams" ON "managers"."teamID" = "teams"."teamID" LEFT OUTER JOIN "seriespost" ON "teams"."teamID" = "seriespost"."teamIDwinner" LEFT OUTER JOIN "els_teamnames" ON "teams"."teamID" = "els_teamnames"."teamid" LEFT OUTER JOIN "batting" ON "teams"."teamID" = "batting"."teamID" LEFT OUTER JOIN "allstarfull" ON "teams"."teamID" = "allstarfull"."teamID" LEFT OUTER JOIN "players" ON "batting"."playerID" = "players"."playerID" LEFT OUTER JOIN "awardsshareplayers" ON "players"."playerID" = "awardsshareplayers"."playerID"  WHERE "managers"."W" >= 35 AND ("managers"."lgID" != '%UA%' OR "managers"."lgID" LIKE '%AL%') AND "awardssharemanagers"."lgID" IN ('NL') AND "managershalf"."managerID" IS NOT NULL AND "awardsmanagers"."managerID" IS NOT NULL AND "seriespost"."teamIDwinner" IS NOT NULL AND "els_teamnames"."teamid" IS NOT NULL AND "allstarfull"."teamID" IS NOT NULL AND "awardsshareplayers"."playerID" IS NOT NULL;