
SELECT SUM("seriespost"."wins") as agg_0 FROM "managershalf" LEFT OUTER JOIN "managers" ON "managershalf"."managerID" = "managers"."managerID" LEFT OUTER JOIN "awardsmanagers" ON "managers"."managerID" = "awardsmanagers"."managerID" LEFT OUTER JOIN "teams" ON "managers"."teamID" = "teams"."teamID" LEFT OUTER JOIN "seriespost" ON "teams"."teamID" = "seriespost"."teamIDwinner" LEFT OUTER JOIN "salaries" ON "teams"."teamID" = "salaries"."teamID" LEFT OUTER JOIN "teamshalf" ON "teams"."teamID" = "teamshalf"."teamID" LEFT OUTER JOIN "allstarfull" ON "teams"."teamID" = "allstarfull"."teamID"  WHERE ("salaries"."salary" >= 593868 OR ("salaries"."lgID" = '%NL%' AND "salaries"."salary" >= 618742)) AND "teamshalf"."W" >= 27 AND "allstarfull"."teamID" NOT LIKE '%MI%N%' AND "teamshalf"."teamID" LIKE '%PIT%' AND "managers"."inseason" <= 1 AND "managers"."managerID" LIKE '%laru%sto01m%' AND "salaries"."yearID" BETWEEN 1985 AND 2010 AND "awardsmanagers"."managerID" IS NOT NULL;