
SELECT MAX("schoolsplayers"."yearMax") as agg_0 FROM "schoolsplayers" LEFT OUTER JOIN "players" ON "schoolsplayers"."playerID" = "players"."playerID" LEFT OUTER JOIN "awardsshareplayers" ON "players"."playerID" = "awardsshareplayers"."playerID" LEFT OUTER JOIN "schools" ON "schoolsplayers"."schoolID" = "schools"."schoolID"  WHERE "schoolsplayers"."schoolID" != '%arktech%' AND "schoolsplayers"."yearMax" >= 1979 AND "players"."birthCountry" IN ('CAN', 'Cuba') AND "schools"."schoolState" = '%LA%' AND "schools"."schoolName" IN ('Tarleton State University', 'William Jewell College', 'Bethel College', 'Azusa Pacific University', 'Concordia College', 'Lubbock Christian University') AND "awardsshareplayers"."playerID" IS NOT NULL;