
SELECT SUM("fieldingpost"."yearID") as agg_0 FROM "awardsshareplayers" LEFT OUTER JOIN "players" ON "awardsshareplayers"."playerID" = "players"."playerID" LEFT OUTER JOIN "fieldingpost" ON "players"."playerID" = "fieldingpost"."playerID" LEFT OUTER JOIN "schoolsplayers" ON "players"."playerID" = "schoolsplayers"."playerID" LEFT OUTER JOIN "schools" ON "schoolsplayers"."schoolID" = "schools"."schoolID" LEFT OUTER JOIN "halloffame" ON "players"."hofID" = "halloffame"."hofID"  WHERE ("schools"."schoolState" != '%AR%' OR "schools"."schoolNick" = '%Delta Devils%') AND "fieldingpost"."PO" = 0 AND "schools"."schoolID" IN ('pugetsound', 'denver') AND "awardsshareplayers"."awardID" != '%MVP%' AND "schools"."schoolName" NOT LIKE '%Louisian%a%' AND "players"."deathYear" BETWEEN 1958.744943552322 AND 1960.201823178354 AND ("fieldingpost"."lgID" != '%AL%' OR "fieldingpost"."lgID" NOT LIKE '%NL%') AND "halloffame"."category" != '%Pioneer/Executive%';