
SELECT MAX("teams"."SB") as agg_0 FROM "managershalf" LEFT OUTER JOIN "managers" ON "managershalf"."managerID" = "managers"."managerID" LEFT OUTER JOIN "awardssharemanagers" ON "managers"."managerID" = "awardssharemanagers"."managerID" LEFT OUTER JOIN "teams" ON "managers"."teamID" = "teams"."teamID" LEFT OUTER JOIN "teamshalf" ON "teams"."teamID" = "teamshalf"."teamID" LEFT OUTER JOIN "salaries" ON "teams"."teamID" = "salaries"."teamID" LEFT OUTER JOIN "seriespost" ON "teams"."teamID" = "seriespost"."teamIDwinner" LEFT OUTER JOIN "allstarfull" ON "teams"."teamID" = "allstarfull"."teamID"  WHERE "salaries"."lgID" NOT LIKE '%A%L%' AND ("allstarfull"."playerID" != '%mcgrifr01%' OR "allstarfull"."playerID" IN ('carewro01', 'musiast01', 'mayswi01')) AND "teams"."name" NOT LIKE '%Seattle%' AND "awardssharemanagers"."managerID" NOT LIKE '%lasorto01m%' AND "teamshalf"."teamID" IS NOT NULL AND "seriespost"."teamIDwinner" IS NOT NULL;