
SELECT SUM("fieldingpost"."G") as agg_0 FROM "players" LEFT OUTER JOIN "pitching" ON "players"."playerID" = "pitching"."playerID" LEFT OUTER JOIN "awardsplayers" ON "players"."playerID" = "awardsplayers"."playerID" LEFT OUTER JOIN "fieldingpost" ON "players"."playerID" = "fieldingpost"."playerID" LEFT OUTER JOIN "appearances" ON "players"."playerID" = "appearances"."playerID"  WHERE "awardsplayers"."yearID" <= 1938 AND "appearances"."lgID" NOT LIKE '%A%A%' AND "awardsplayers"."notes" != '%Ss%' AND "awardsplayers"."notes" LIKE '%P%1%' AND "players"."weight" IS NULL AND "pitching"."playerID" IS NOT NULL;