
SELECT MIN("pitching"."G") as agg_0 FROM "schools" LEFT OUTER JOIN "schoolsplayers" ON "schools"."schoolID" = "schoolsplayers"."schoolID" LEFT OUTER JOIN "players" ON "schoolsplayers"."playerID" = "players"."playerID" LEFT OUTER JOIN "pitchingpost" ON "players"."playerID" = "pitchingpost"."playerID" LEFT OUTER JOIN "pitching" ON "players"."playerID" = "pitching"."playerID" LEFT OUTER JOIN "fieldingof" ON "players"."playerID" = "fieldingof"."playerID" LEFT OUTER JOIN "awardsplayers" ON "players"."playerID" = "awardsplayers"."playerID" LEFT OUTER JOIN "halloffame" ON "players"."hofID" = "halloffame"."hofID" LEFT OUTER JOIN "appearances" ON "players"."playerID" = "appearances"."playerID" LEFT OUTER JOIN "awardsshareplayers" ON "players"."playerID" = "awardsshareplayers"."playerID" LEFT OUTER JOIN "batting" ON "players"."playerID" = "batting"."playerID"  WHERE "halloffame"."yearID" = 1953 AND "halloffame"."ballots" <= 514.6752214467746 AND "awardsshareplayers"."lgID" LIKE '%A%L%' AND "batting"."G_batting" BETWEEN 0.5873302433909819 AND 119.9095158676644 AND "pitchingpost"."playerID" IS NOT NULL AND "fieldingof"."playerID" IS NOT NULL AND "awardsplayers"."playerID" IS NOT NULL AND "appearances"."playerID" IS NOT NULL;