
SELECT MAX("teams"."CG") as agg_0 FROM "awardsshareplayers" LEFT OUTER JOIN "players" ON "awardsshareplayers"."playerID" = "players"."playerID" LEFT OUTER JOIN "battingpost" ON "players"."playerID" = "battingpost"."playerID" LEFT OUTER JOIN "halloffame" ON "players"."hofID" = "halloffame"."hofID" LEFT OUTER JOIN "batting" ON "players"."playerID" = "batting"."playerID" LEFT OUTER JOIN "teams" ON "batting"."teamID" = "teams"."teamID"  WHERE "teams"."franchID" NOT LIKE '%TE%X%' AND "halloffame"."inducted" = '%N%' AND "battingpost"."lgID" LIKE '%AL%' AND "battingpost"."HR" >= 0;