
SELECT COUNT(*) as agg_0 FROM "fieldingpost" LEFT OUTER JOIN "players" ON "fieldingpost"."playerID" = "players"."playerID" LEFT OUTER JOIN "halloffame" ON "players"."hofID" = "halloffame"."hofID" LEFT OUTER JOIN "appearances" ON "players"."playerID" = "appearances"."playerID" LEFT OUTER JOIN "pitchingpost" ON "players"."playerID" = "pitchingpost"."playerID" LEFT OUTER JOIN "schoolsplayers" ON "players"."playerID" = "schoolsplayers"."playerID" LEFT OUTER JOIN "pitching" ON "players"."playerID" = "pitching"."playerID" LEFT OUTER JOIN "battingpost" ON "players"."playerID" = "battingpost"."playerID" LEFT OUTER JOIN "batting" ON "players"."playerID" = "batting"."playerID" LEFT OUTER JOIN "awardsplayers" ON "players"."playerID" = "awardsplayers"."playerID"  WHERE "schoolsplayers"."schoolID" IN ('michigan', 'texas', 'holycross', 'stmarysca', 'notredame', 'usc', 'ucla', 'stanford') AND "pitching"."teamID" != '%SLU%' AND ("battingpost"."teamID" LIKE '%S%DN%' OR "battingpost"."SF" >= 0.0 OR "battingpost"."teamID" NOT LIKE '%NYN%') AND "halloffame"."hofID" IS NOT NULL AND "appearances"."playerID" IS NOT NULL AND "pitchingpost"."playerID" IS NOT NULL AND "batting"."playerID" IS NOT NULL AND "awardsplayers"."playerID" IS NOT NULL;