
SELECT MIN("batting"."SF") as agg_0 FROM "players" LEFT OUTER JOIN "halloffame" ON "players"."hofID" = "halloffame"."hofID" LEFT OUTER JOIN "appearances" ON "players"."playerID" = "appearances"."playerID" LEFT OUTER JOIN "awardsplayers" ON "players"."playerID" = "awardsplayers"."playerID" LEFT OUTER JOIN "schoolsplayers" ON "players"."playerID" = "schoolsplayers"."playerID" LEFT OUTER JOIN "battingpost" ON "players"."playerID" = "battingpost"."playerID" LEFT OUTER JOIN "fieldingpost" ON "players"."playerID" = "fieldingpost"."playerID" LEFT OUTER JOIN "batting" ON "players"."playerID" = "batting"."playerID" LEFT OUTER JOIN "schools" ON "schoolsplayers"."schoolID" = "schools"."schoolID"  WHERE "players"."bats" != '%L%' AND "battingpost"."3B" >= 0 AND "halloffame"."votes" IS NOT NULL AND ("fieldingpost"."round" != '%ALDS1%' OR "fieldingpost"."round" LIKE '%ALDS2%' OR "fieldingpost"."round" LIKE '%NLDS1%') AND "halloffame"."category" IN ('Player', 'Pioneer/Executive', 'Umpire') AND "schoolsplayers"."schoolID" NOT LIKE '%illinois%' AND "players"."college" = '%Baylor%' AND "appearances"."playerID" IS NOT NULL AND "awardsplayers"."playerID" IS NOT NULL AND "schools"."schoolID" IS NOT NULL;