
SELECT SUM("fielding"."PB") as agg_0 FROM "fielding" LEFT OUTER JOIN "players" ON "fielding"."playerID" = "players"."playerID" LEFT OUTER JOIN "awardsplayers" ON "players"."playerID" = "awardsplayers"."playerID" LEFT OUTER JOIN "pitchingpost" ON "players"."playerID" = "pitchingpost"."playerID" LEFT OUTER JOIN "battingpost" ON "players"."playerID" = "battingpost"."playerID" LEFT OUTER JOIN "fieldingof" ON "players"."playerID" = "fieldingof"."playerID" LEFT OUTER JOIN "halloffame" ON "players"."hofID" = "halloffame"."hofID"  WHERE "awardsplayers"."notes" LIKE '%3B%' AND "pitchingpost"."lgID" LIKE '%NL%' AND "fieldingof"."Grf" <= 0.0 AND "battingpost"."playerID" IS NOT NULL AND "halloffame"."hofID" IS NOT NULL;