
SELECT COUNT(*) as agg_0 FROM "appearances" LEFT OUTER JOIN "players" ON "appearances"."playerID" = "players"."playerID" LEFT OUTER JOIN "fieldingof" ON "players"."playerID" = "fieldingof"."playerID" LEFT OUTER JOIN "awardsshareplayers" ON "players"."playerID" = "awardsshareplayers"."playerID" LEFT OUTER JOIN "fieldingpost" ON "players"."playerID" = "fieldingpost"."playerID"  WHERE ("fieldingpost"."POS" IN ('DH', '2B', 'C', 'LF', '3B', '1B', 'CF') OR ("fieldingpost"."POS" IN ('CF') AND "fieldingpost"."POS" NOT LIKE '%1B%')) AND "fieldingpost"."DP" BETWEEN 0 AND 1 AND "players"."birthState" = '%19%' AND "players"."college" IN ('Arizona', 'Miami (FL)', 'Florida State', 'Arizona State', 'Texas', 'None', 'USC', 'UCLA', 'Cal St. Fullerton') AND "players"."nameFirst" LIKE '%Joh%n%' AND "fieldingpost"."G" <= 3 AND "fieldingof"."playerID" IS NOT NULL AND "awardsshareplayers"."playerID" IS NOT NULL;